import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-list',
  templateUrl: './user-list.component.html',
  styleUrls: ['./user-list.component.scss']
})
export class UserListComponent implements OnInit {
  users: any[] = [];
  searchText: string = '';

  constructor(private http: HttpClient, private router: Router) { }

  viewDetails(userId: number) {
    this.router.navigate(['/users', userId]);
  }

  ngOnInit(): void {
    this.fetchUsers();
  }

  fetchUsers() {
    this.http.get<any>('https://reqres.in/api/users').subscribe(response => {
      this.users = response.data;
    });
  }

  get filteredUsers() {
    return this.users.filter(user =>
      // user.first_name.toLowerCase().includes(this.searchText.toLowerCase()) ||
      // user.last_name.toLowerCase().includes(this.searchText.toLowerCase()) ||
      user.id.toString().toLowerCase().includes(this.searchText.toLowerCase())
    );
  }
}
<div class="container">
  <div class="row pt-5">
    <div class="col-md-12">
      <input type="text" class="form-control" placeholder="Search By Id ..." [(ngModel)]="searchText">
    </div>
  </div>
  <div *ngIf="filteredUsers.length > 0; else noUserFound">
    <table class="table table-bordered mt-3">
      <thead>
        <tr>
          <th>#</th>
          <th>First Name</th>
          <th>Last Name</th>
          <th>Avatar</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let user of filteredUsers;">
          <td>{{ user.id }}</td>
          <td>{{ user.first_name }}</td>
          <td>{{ user.last_name }}</td>
          <td><img [src]="user.avatar" alt="Avatar" class="img-thumbnail"></td>
          <td><button (click)="viewDetails(user.id)" class="btn btn-secondary">View Details</button></td>
        </tr>
      </tbody>
    </table>
  </div>
  <ng-template #noUserFound>
    <div class="alert alert-warning mt-3" role="alert">
      No user found with the specified name or ID.
    </div>
  </ng-template>
</div>
